import React, { Component } from 'react'

class Table extends Component {
  constructor(props) {
    super(props)
    this.state = {
      page: 0,
    };
  }

  handlePreviousClick = (e) => {
    e.preventDefault()  
    this.setState({page: this.state.page - 1})
  }

  handleNextClick = (e) => {
    e.preventDefault() 
    this.setState({page: this.state.page + 1})
  }

  render() {
    const header = this.props.columns.map((col) => {
      return (
        <th>{col.name}</th>
      );
    });

    const first = this.state.page * 25

    const displayRows = this.props.rows.slice(first, first + 25).map((row) => {
      const cell = this.props.columns.map((col) => {
        const value = row[col.property] 
        return <td>{this.props.format(col.property, value)}</td>
      })

      return (
        <tr>
          {cell}
        </tr>
      )
    });

    const pagination = (
      <div className='pagination'>
        <p>
          Showing {first}-{first + 25} of {this.props.rows.length} routes.
        </p>
        <p>
          <button 
            key='previous' 
            onClick={this.handlePreviousClick}
          >
            Previous
          </button>

          <button 
            key='next'
            onClick={this.handleNextClick}
          >
            Next
          </button>
        </p>
      </div>
    )

    return (
      <div>
        <table className='routes-table'>
          <thead>
            <tr>
              {header}
            </tr>
          </thead>
          <tbody>
            {displayRows}
          </tbody>
        </table>
        {pagination}
      </div>
    );
  }
}

export default Table
