import React, { Component } from 'react'

class Table extends Component {
  render() {
    const displayTableHeader = this.props.columns.map((col) => {
      return (
        <th key={col.name}>{col.name}</th>
      );
    });

    const routeIdx = (this.props.page - 1) * this.props.perPage
    const totalPages = () => {
      const total = Math.ceil(this.props.rows.length / this.props.perPage)
      return total > 0 ? total : 1
    }

    const displayRows = this.props.rows.slice(
      routeIdx, (routeIdx + this.props.perPage)
    ).map((row, idx) => {
      const cell = this.props.columns.map((col) => {
        const value = row[col.property] 
        return <td key={value}>{this.props.format(col.property, value)}</td>
      })

      return (
        <tr key={idx}>
          {cell}
        </tr>
      )
    });

    const displayPagination = (
      <div className='pagination'>
        <p>
          Showing page {this.props.page} of {totalPages} (
            {this.props.rows.length} routes)
        </p>
        <p>
          <button 
            key='previous' 
            onClick={this.props.prevClick}
            disabled={routeIdx === 0}
          >
            Previous
          </button>
          &nbsp;
          <button 
            key='next'
            onClick={this.props.nextClick}
            disabled={routeIdx + this.props.perPage >= this.props.rows.length}
          >
            Next
          </button>
        </p>
      </div>
    )

    return (
      <div>
        <table className='routes-table'>
          <thead>
            <tr>
              {displayTableHeader}
            </tr>
          </thead>
          <tbody>
            {displayRows}
          </tbody>
        </table>
        {displayPagination}
      </div>
    );
  }
}

export default Table
