import React, { Component } from 'react'

class Table extends Component {
  render() {
    const header = this.props.columns.map((col) => {
      return (
        <th>{col.name}</th>
      );
    });

    const row = this.props.rows.map((route) => { 
      const airlineName = this.props.format('airline', route.airline);
      const srcName = this.props.format('airport', route.src);
      const destName = this.props.format('airport', route.dest)

      return (
        <tr>
          <td>{route.airlineName}</td>
          <td>{route.srcName}</td>
          <td>{route.destName}</td>
        </tr>
      )
    })

    return (
      <table className='routes-table'>
        <thead>
          <tr>
            {header}
          </tr>
        </thead>
        <tbody>
          {row}
        </tbody>
      </table>
    );
  }
}

export default Table
