import React, { Component } from 'react'

class Table extends Component {
  constructor(props) {
    super(props)
    this.state = {
      page: 0,
    };
  }

  handlePreviousClick = (e) => {
    e.preventDefault()  
    this.setState({page: this.state.page - 1})
  }

  handleNextClick = (e) => {
    e.preventDefault() 
    this.setState({page: this.state.page + 1})
  }

  render() {
    const displayTableHeader = this.props.columns.map((col) => {
      return (
        <th key={col.name}>{col.name}</th>
      );
    });

    const routeIdx = this.state.page * this.props.perPage

    const displayRows = this.props.rows.slice(
      routeIdx, (routeIdx + this.props.perPage)
    ).map((row, idx) => {
      const cell = this.props.columns.map((col) => {
        const value = row[col.property] 
        return <td key={value}>{this.props.format(col.property, value)}</td>
      })

      return (
        <tr key={idx}>
          {cell}
        </tr>
      )
    });

    const displayPagination = (
      <div className='pagination'>
        <p>
          Showing page {this.state.page + 1} of
          {Math.round(this.props.rows.length / this.props.perPage) + 1}
          ({this.props.rows.length} routes)
        </p>
        <p>
          <button 
            key='previous' 
            onClick={this.handlePreviousClick}
            disabled={routeIdx === 0}
          >
            Previous
          </button>

          <button 
            key='next'
            onClick={this.handleNextClick}
            disabled={routeIdx + this.props.perPage >= this.props.rows.length}
          >
            Next
          </button>
        </p>
      </div>
    )

    return (
      <div>
        <table className='routes-table'>
          <thead>
            <tr>
              {displayTableHeader}
            </tr>
          </thead>
          <tbody>
            {displayRows}
          </tbody>
        </table>
        {displayPagination}
      </div>
    );
  }
}

export default Table
