import React, { Component } from 'react'
import { getAirportByCode } from './../data'

class Map extends Component {
  render() {
    const displayRoute = this.props.routes.map((route) => {
      const srcAirport = getAirportByCode(route.src) 
      const destAirport = getAirportByCode(route.dest)
      const linePath = `M${srcAirport.long} ${srcAirport.lat} `
        + `L${destAirport.long} ${destAirport.lat}`

      return (
        <g key={route.airline + route.src + route.dest}>
          <circle className='source' cx={srcAirport.long} cy={srcAirport.lat}>
            <title>{srcAirport.name}</title>
          </circle>
          <circle className='destination' cx={destAirport.long} 
            cy={destAirport.lat}>
            <title>{destAirport.name}</title>
          </circle>
          <path d={linePath} />
        </g>
      )
    })

    return (
      <svg className="map" viewBox="-180 -90 360 180">
        <g transform="scale(1 -1)">
          <image xlinkHref="equirectangular_world.jpg" href="equirectangular_world.jpg" x="-180" y="-90" height="100%" width="100%" transform="scale(1 -1)"/>
          {displayRoute}  
        </g>
      </svg>
    )
  }
}

export default Map
