import React, { Component } from 'react';
import './App.css';
import Data from './data.js'
import Table from './components/Table'
import Filter from './components/Filter'

class App extends Component {
  constructor(props) {
    super(props)
    state = {
      airline: 'all',
      airport: 'all',
    };
  }

  formatValue(property, value) {
    if (property === 'airline') {
      return Data.getAirlineById(value)
    } else if (property === 'src' || property === 'dest') {
      return Data.getAirportByCode(value)
    }
  }

  render() {
    const columns = [
      {name: 'Airline', property: 'airline'},
      {name: 'Source Airport', property: 'src'},
      {name: 'Destination Airport', property: 'dest'},
    ];

    const filterRoutesByAirline = Data.routes.filter((route) => {
      return route.airline === this.state.airline;
    });

    const filterRoutesByAirport = Data.routes.filter((route) => {
      return route.airport === this.state.airport;
    });

    return (
      <div className="app">
        <header className="header">
          <h1 className="title">Airline Routes</h1>
        </header>
        <section>
          <p>
            Show only routes on 
            <Filter 
              options={filterRoutesByAirline}
            />
            at
            <Filter 
              options={filterRoutesByAirport}
            />
          </p>
          <Table 
            columns={columns} 
            rows={Data.routes}
            format={this.formatValue}
            perPage={25}
          />
        </section>
      </div>
    );
  }
}

export default App;
