import React, { Component } from 'react'

class Table extends Component {
  constructor(props) {
    super(props)
    this.state = {
      page: 0,
    };
  }

  render() {
    const header = this.props.columns.map((col) => {
      return (
        <th>{col.name}</th>
      );
    });

    const displayRows = this.props.rows.slice(0, 25).map((row) => {
      const cell = this.props.columns.map((col) => {
        const value = row[col.property] 
        return <td>{this.props.format(col.property, value)}</td>
      })

      return (
        <tr>
          {cell}
        </tr>
      )
    });

    const pagination = (
      <div className='pagination'>
        <p>
          Showing 1-25 of {this.props.rows.length} routes.
        </p>
      </div>
    )

    return (
      <div>
        <table className='routes-table'>
          <thead>
            <tr>
              {header}
            </tr>
          </thead>
          <tbody>
            {displayRows}
          </tbody>
        </table>
        {pagination}
      </div>
    );
  }
}

export default Table
